import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import DarkModeToggle from './DarkModeToggle';
import NotificationBell from './NotificationBell';
import ProfileDropdown from './ProfileDropdown';

function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  return (
    <nav className="bg-white dark:bg-gray-800 shadow-md transition-colors">
      <div className="container mx-auto px-4 py-4">
        <div className="flex justify-between items-center">
          {/* Logo */}
          <h1 
            className="text-xl sm:text-2xl font-bold text-primary dark:text-blue-400 cursor-pointer" 
            onClick={() => navigate('/')}
          >
            JobBoard
          </h1>

          {/* Right side - Auth buttons or User menu */}
          <div className="flex gap-3 items-center">
            <DarkModeToggle />
            
            {user ? (
              <>
                <NotificationBell />
                <ProfileDropdown user={user} onLogout={logout} />
              </>
            ) : (
              <>
                <button
                  onClick={() => navigate('/login')}
                  className="px-4 py-2 text-primary dark:text-blue-400 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition font-semibold"
                >
                  Login
                </button>
                <button
                  onClick={() => navigate('/register')}
                  className="px-4 py-2 bg-primary dark:bg-blue-600 text-white rounded-lg hover:bg-blue-600 dark:hover:bg-blue-700 transition font-semibold"
                >
                  Sign Up
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
