import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { getAllJobs } from '../services/api';

function Home() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const response = await getAllJobs();
      setJobs(response.data.jobs);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setLoading(false);
    }
  };

const handleApply = (jobId) => {
  if (!user) {
    navigate('/login');
    return;
  }
  if (user.user_type !== 'job_seeker') {
    alert('Only job seekers can apply to jobs');
    return;
  }
  navigate(`/apply/${jobId}`);
};

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navigation Bar */}
      <nav className="bg-white shadow-md">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-primary cursor-pointer" onClick={() => navigate('/')}>
            JobBoard
          </h1>
          <div className="flex gap-4 items-center">
            {user ? (
              <>
                <span className="text-gray-700">Welcome, <span className="font-semibold">{user.full_name}</span></span>
                <button 
                  onClick={() => navigate('/dashboard')} 
                  className="px-4 py-2 bg-primary text-white rounded-lg hover:bg-blue-600 transition"
                >
                  Dashboard
                </button>
                <button 
                  onClick={logout} 
                  className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition"
                >
                  Logout
                </button>
              </>
            ) : (
              <>
                <button 
                  onClick={() => navigate('/login')} 
                  className="px-4 py-2 text-gray-700 hover:text-primary transition"
                >
                  Login
                </button>
                <button 
                  onClick={() => navigate('/register')} 
                  className="px-6 py-2 bg-primary text-white rounded-lg hover:bg-blue-600 transition"
                >
                  Sign Up
                </button>
              </>
            )}
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="bg-gradient-to-r from-blue-500 to-blue-700 text-white py-20">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-5xl font-bold mb-4">Find Your Dream Job</h1>
          <p className="text-xl">Connect with top companies and start your career journey today</p>
        </div>
      </section>

      {/* Jobs Listing */}
      <section className="container mx-auto px-4 py-12">
        <h2 className="text-3xl font-bold mb-8 text-gray-800">Available Jobs</h2>
        
        {loading ? (
          <p className="text-gray-600">Loading jobs...</p>
        ) : jobs.length === 0 ? (
          <div className="bg-white p-8 rounded-lg shadow text-center">
            <p className="text-gray-600">No jobs available at the moment.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {jobs.map((job) => (
              <div key={job.id} className="bg-white rounded-lg shadow-md p-6 hover:shadow-xl transition">
                <h3 className="text-xl font-bold text-gray-800 mb-2">{job.title}</h3>
                <p className="text-primary font-semibold mb-2">{job.company}</p>
                <div className="flex items-center text-gray-600 mb-2">
                  <span className="mr-2">üìç</span>
                  <span>{job.location}</span>
                </div>
                <span className="inline-block bg-blue-100 text-primary px-3 py-1 rounded-full text-sm mb-3">
                  {job.job_type}
                </span>
                {job.salary_range && (
                  <p className="text-secondary font-semibold mb-3">üí∞ {job.salary_range}</p>
                )}
                <p className="text-gray-600 mb-4 line-clamp-3">{job.description}</p>
                <button 
                  onClick={() => handleApply(job.id)} 
                  className="w-full bg-primary text-white py-2 rounded-lg hover:bg-blue-600 transition font-semibold"
                >
                  Apply Now
                </button>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

export default Home;
