import { calculateJobMatch } from '../utils/jobMatchingAlgorithm';
import Navbar from '../components/Navbar';

function Home() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [jobsList, setJobsList] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [locationFilter, setLocationFilter] = useState('');
  const [jobTypeFilter, setJobTypeFilter] = useState('all');
  const [workModeFilter, setWorkModeFilter] = useState('all');
  const [savedJobIds, setSavedJobIds] = useState(new Set());
  const [sortBy, setSortBy] = useState('recent');
  const [profile, setProfile] = useState(null);
  const [jobMatches, setJobMatches] = useState({});

  const showAIFeatures = user?.user_type === 'job_seeker' && profile;

  useEffect(() => {
    fetchJobs();
    if (user?.user_type === 'job_seeker') {
      fetchProfile();
    }
  }, [user]);

  useEffect(() => {
    applyFilters();
  }, [searchTerm, locationFilter, jobTypeFilter, workModeFilter, jobsList, sortBy, jobMatches]);

  const fetchProfile = async () => {
    try {
      const response = await getMyProfile();
      setProfile(response.data);
    } catch (error) {
      console.log('No profile found');
    }
  };

  const fetchJobs = async () => {
    try {
      const response = await getAllJobs();
      setJobsList(response.data);
      
      if (user?.user_type === 'job_seeker') {
        const savedPromises = response.data.map(job => 
          checkIfJobSaved(job.id).then(res => ({ jobId: job.id, saved: res.data.saved }))
        );
        const savedResults = await Promise.all(savedPromises);
        const savedSet = new Set(savedResults.filter(r => r.saved).map(r => r.jobId));
        setSavedJobIds(savedSet);
      }
      
      setLoading(false);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    if (profile && jobsList.length > 0) {
      const matches = {};
      jobsList.forEach(job => {
        matches[job.id] = calculateJobMatch(profile, job);
      });
      setJobMatches(matches);
    }
  }, [profile, jobsList]);

  const applyFilters = () => {
    let filtered = [...jobsList]; // Create a copy as array

    if (searchTerm) {
      filtered = filtered.filter(job =>
        job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        job.company.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (locationFilter) {
      filtered = filtered.filter(job =>
        job.location.toLowerCase().includes(locationFilter.toLowerCase())
      );
    }

    if (jobTypeFilter !== 'all') {
      filtered = filtered.filter(job => job.job_type === jobTypeFilter);
    }

    if (workModeFilter !== 'all') {
      filtered = filtered.filter(job => job.work_mode === workModeFilter);
    }

    if (sortBy === 'recommended' && showAIFeatures) {
      filtered = filtered.sort((a, b) => {
        const scoreA = jobMatches[a.id]?.score || 0;
        const scoreB = jobMatches[b.id]?.score || 0;
        return scoreB - scoreA;
      });
    } else {
      filtered = filtered.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
    }

    setFilteredJobs(filtered);
  };

  const handleSaveToggle = async (jobId, e) => {
    e.stopPropagation();
    try {
      if (savedJobIds.has(jobId)) {
        await unsaveJob(jobId);
        setSavedJobIds(prev => {
          const newSet = new Set(prev);
          newSet.delete(jobId);
          return newSet;
        });
      } else {
        await saveJob(jobId);
        setSavedJobIds(prev => new Set(prev).add(jobId));
      }
    } catch (error) {
      console.error('Error toggling save:', error);
      alert('Failed to save/unsave job');
    }
  };

  const getMatchColor = (score) => {
    if (score >= 85) return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
    if (score >= 70) return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
    if (score >= 55) return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
    if (score >= 40) return 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200';
    return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
  };

  const getWorkModeIcon = (workMode) => {
    switch(workMode) {
      case 'remote': return 'üè†';
      case 'hybrid': return 'üîÑ';
      case 'on-site': return 'üè¢';
      default: return 'üìç';
    }
  };

  const getWorkModeColor = (workMode) => {
    switch(workMode) {
      case 'remote': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'hybrid': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'on-site': return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors">
      <Navbar />

      {/* Hero Section */}
      <section className="bg-gradient-to-r from-blue-500 to-blue-700 dark:from-blue-800 dark:to-blue-900 text-white py-12 sm:py-20 transition-colors">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-3xl sm:text-5xl font-bold mb-4">Find Your Dream Job</h1>
          <p className="text-lg sm:text-xl">Connect with top companies and start your career journey today</p>
        </div>
      </section>

      {/* Profile Completion Banner */}
      {user?.user_type === 'job_seeker' && !profile && (
        <div className="bg-gradient-to-r from-purple-500 to-purple-700 dark:from-purple-800 dark:to-purple-900 text-white py-4">
          <div className="container mx-auto px-4 flex items-center justify-between">
            <div className="flex items-center gap-3">
              <span className="text-2xl">ü§ñ</span>
              <p className="font-semibold">Complete your profile to unlock AI-powered job matching!</p>
            </div>
            <button
              onClick={() => navigate('/profile')}
              className="bg-white text-purple-700 px-6 py-2 rounded-lg font-semibold hover:bg-gray-100 transition"
            >
              Complete Profile
            </button>
          </div>
        </div>
      )}

      {/* Search and Filters */}
      <section className="container mx-auto px-4 py-8">
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-4 sm:p-6 mb-8 transition-colors">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
            <input
              type="text"
              placeholder="Search jobs..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
            />
            <input
              type="text"
              placeholder="Location"
              value={locationFilter}
              onChange={(e) => setLocationFilter(e.target.value)}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
            />
            <select
              value={jobTypeFilter}
              onChange={(e) => setJobTypeFilter(e.target.value)}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
            >
              <option value="all">All Job Types</option>
              <option value="full-time">Full-time</option>
              <option value="part-time">Part-time</option>
              <option value="contract">Contract</option>
              <option value="flexible">Flexible</option>
            </select>
            <select
              value={workModeFilter}
              onChange={(e) => setWorkModeFilter(e.target.value)}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
            >
              <option value="all">All Work Modes</option>
              <option value="on-site">On-site</option>
              <option value="remote">Remote</option>
              <option value="hybrid">Hybrid</option>
            </select>
            {showAIFeatures && (
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
              >
                <option value="recommended">ü§ñ Recommended</option>
                <option value="recent">üìÖ Most Recent</option>
              </select>
            )}
          </div>
        </div>

        {/* Jobs List */}
        {loading ? (
          <p className="text-center text-gray-600 dark:text-gray-400">Loading jobs...</p>
        ) : filteredJobs.length === 0 ? (
          <p className="text-center text-gray-600 dark:text-gray-400">No jobs found matching your criteria.</p>
        ) : (
          <div className="grid grid-cols-1 gap-6">
            {filteredJobs.map((job) => {
              const match = jobMatches[job.id];
              return (
                <div
                  key={job.id}
                  onClick={() => navigate(`/apply/${job.id}`)}
                  className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6 hover:shadow-lg transition cursor-pointer"
                >
                  <div className="flex justify-between items-start mb-4">
                    <div className="flex-1">
                      <h3 className="text-xl font-bold text-gray-800 dark:text-gray-100 mb-2">{job.title}</h3>
                      <p className="text-primary dark:text-blue-400 font-semibold mb-2">{job.company}</p>
                      <div className="flex flex-wrap gap-2 text-sm text-gray-600 dark:text-gray-400">
                        <span className="flex items-center gap-1">
                          üìç {job.location}
                        </span>
                        <span className={`px-2 py-1 rounded-full text-xs font-semibold ${getWorkModeColor(job.work_mode)}`}>
                          {getWorkModeIcon(job.work_mode)} {job.work_mode?.charAt(0).toUpperCase() + job.work_mode?.slice(1)}
                        </span>
                        <span>‚Ä¢ {job.job_type}</span>
                        {job.salary_range && <span>‚Ä¢ üí∞ {job.salary_range}</span>}
                      </div>
                    </div>

                    <div className="flex flex-col items-end gap-2">
                      {user?.user_type === 'job_seeker' && (
                        <button
                          onClick={(e) => handleSaveToggle(job.id, e)}
                          className="text-2xl hover:scale-110 transition-transform"
                        >
                          {savedJobIds.has(job.id) ? (
                            <svg className="w-7 h-7 text-red-500" fill="currentColor" viewBox="0 0 20 20">
                              <path fillRule="evenodd" d="M3.172 5.172a4 4 0 015.656 0L10 6.343l1.172-1.171a4 4 0 115.656 5.656L10 17.657l-6.828-6.829a4 4 0 010-5.656z" clipRule="evenodd" />
                            </svg>
                          ) : (
                            <svg className="w-7 h-7 text-gray-400 hover:text-red-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                            </svg>
                          )}
                        </button>
                      )}
                      
                      {showAIFeatures && match && (
                        <span className={`px-3 py-1 rounded-full text-xs font-bold ${getMatchColor(match.score)}`}>
                          {match.percentage}% Match
                        </span>
                      )}
                    </div>
                  </div>

                  <p className="text-gray-700 dark:text-gray-300 mb-4 line-clamp-2">{job.description}</p>

                  {showAIFeatures && match && match.topReasons.length > 0 && (
                    <div className="mt-4 p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                      <p className="text-sm font-semibold text-blue-800 dark:text-blue-300 mb-2">üéØ Why this job:</p>
                      <ul className="text-sm text-blue-700 dark:text-blue-400 space-y-1">
                        {match.topReasons.map((reason, idx) => (
                          <li key={idx}>‚Ä¢ {reason}</li>
                        ))}
                      </ul>
                    </div>
                  )}

                  <div className="mt-4 flex justify-between items-center text-sm text-gray-500 dark:text-gray-500">
                    <span>Posted {new Date(job.created_at).toLocaleDateString()}</span>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>
    </div>
  );
}

export default Home;
