import React, { useState, useEffect, useContext } from 'react';
import { getMyJobs, createJob, deleteJob, getJobApplications } from '../services/api';
import { AuthContext } from '../context/AuthContext';

function RecruiterDashboard() {
  const [jobs, setJobs] = useState([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext);

  const [newJob, setNewJob] = useState({
    title: '',
    description: '',
    company: user?.company_name || '',
    location: '',
    job_type: 'Full-time',
    salary_range: '',
    requirements: ''
  });

  useEffect(() => {
    fetchMyJobs();
  }, []);

  const fetchMyJobs = async () => {
    try {
      const response = await getMyJobs();
      setJobs(response.data.jobs);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setLoading(false);
    }
  };

  const handleCreateJob = async (e) => {
    e.preventDefault();
    try {
      await createJob(newJob);
      setShowCreateForm(false);
      setNewJob({
        title: '',
        description: '',
        company: user?.company_name || '',
        location: '',
        job_type: 'Full-time',
        salary_range: '',
        requirements: ''
      });
      fetchMyJobs();
      alert('Job posted successfully!');
    } catch (error) {
      alert('Error creating job: ' + (error.response?.data?.error || error.message));
    }
  };

  const handleDeleteJob = async (jobId) => {
    if (window.confirm('Are you sure you want to delete this job?')) {
      try {
        await deleteJob(jobId);
        fetchMyJobs();
        alert('Job deleted successfully!');
      } catch (error) {
        alert('Error deleting job: ' + (error.response?.data?.error || error.message));
      }
    }
  };

  const viewApplications = async (job) => {
    setSelectedJob(job);
    try {
      const response = await getJobApplications(job.id);
      setApplications(response.data.applications);
    } catch (error) {
      console.error('Error fetching applications:', error);
      alert('Error loading applications');
    }
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800">My Job Posts</h1>
        <button 
          onClick={() => setShowCreateForm(!showCreateForm)}
          className="px-6 py-3 bg-primary text-white rounded-lg hover:bg-blue-600 transition font-semibold"
        >
          {showCreateForm ? 'Cancel' : '+ Post New Job'}
        </button>
      </div>

      {/* Create Job Form */}
      {showCreateForm && (
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h2 className="text-2xl font-bold mb-4">Create New Job</h2>
          <form onSubmit={handleCreateJob}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-gray-700 font-semibold mb-2">Job Title *</label>
                <input
                  type="text"
                  value={newJob.title}
                  onChange={(e) => setNewJob({...newJob, title: e.target.value})}
                  required
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                  placeholder="e.g. Senior Developer"
                />
              </div>

              <div>
                <label className="block text-gray-700 font-semibold mb-2">Company *</label>
                <input
                  type="text"
                  value={newJob.company}
                  onChange={(e) => setNewJob({...newJob, company: e.target.value})}
                  required
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                />
              </div>

              <div>
                <label className="block text-gray-700 font-semibold mb-2">Location *</label>
                <input
                  type="text"
                  value={newJob.location}
                  onChange={(e) => setNewJob({...newJob, location: e.target.value})}
                  required
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                  placeholder="e.g. Remote, New York"
                />
              </div>

              <div>
                <label className="block text-gray-700 font-semibold mb-2">Job Type *</label>
                <select
                  value={newJob.job_type}
                  onChange={(e) => setNewJob({...newJob, job_type: e.target.value})}
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                >
                  <option>Full-time</option>
                  <option>Part-time</option>
                  <option>Contract</option>
                  <option>Remote</option>
                </select>
              </div>

              <div>
                <label className="block text-gray-700 font-semibold mb-2">Salary Range</label>
                <input
                  type="text"
                  value={newJob.salary_range}
                  onChange={(e) => setNewJob({...newJob, salary_range: e.target.value})}
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                  placeholder="e.g. $80k - $120k"
                />
              </div>
            </div>

            <div className="mt-4">
              <label className="block text-gray-700 font-semibold mb-2">Job Description *</label>
              <textarea
                value={newJob.description}
                onChange={(e) => setNewJob({...newJob, description: e.target.value})}
                required
                rows="4"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                placeholder="Describe the role..."
              />
            </div>

            <div className="mt-4">
              <label className="block text-gray-700 font-semibold mb-2">Requirements *</label>
              <textarea
                value={newJob.requirements}
                onChange={(e) => setNewJob({...newJob, requirements: e.target.value})}
                required
                rows="3"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                placeholder="List requirements..."
              />
            </div>

            <button 
              type="submit"
              className="mt-4 px-6 py-3 bg-secondary text-white rounded-lg hover:bg-green-600 transition font-semibold"
            >
              Post Job
            </button>
          </form>
        </div>
      )}

      {/* Jobs List */}
      {loading ? (
        <p>Loading your jobs...</p>
      ) : jobs.length === 0 ? (
        <div className="bg-white rounded-lg shadow p-8 text-center">
          <p className="text-gray-600">You haven't posted any jobs yet.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-6">
          {jobs.map((job) => (
            <div key={job.id} className="bg-white rounded-lg shadow-md p-6">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <h3 className="text-2xl font-bold text-gray-800">{job.title}</h3>
                  <p className="text-primary font-semibold">{job.company}</p>
                  <p className="text-gray-600">üìç {job.location} ‚Ä¢ {job.job_type}</p>
                  {job.salary_range && <p className="text-secondary">üí∞ {job.salary_range}</p>}
                  <p className="text-gray-700 mt-3">{job.description}</p>
                  <p className="text-sm text-gray-500 mt-2">Posted: {new Date(job.created_at).toLocaleDateString()}</p>
                </div>
                <div className="flex flex-col gap-2 ml-4">
                  <button 
                    onClick={() => viewApplications(job)}
                    className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition text-sm"
                  >
                    View Applications
                  </button>
                  <button 
                    onClick={() => handleDeleteJob(job.id)}
                    className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition text-sm"
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Applications Modal */}
      {selectedJob && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto p-6">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-2xl font-bold">Applications for: {selectedJob.title}</h2>
              <button 
                onClick={() => setSelectedJob(null)}
                className="text-gray-500 hover:text-gray-700 text-2xl"
              >
                ‚úï
              </button>
            </div>
            
            {applications.length === 0 ? (
              <p className="text-gray-600">No applications yet.</p>
            ) : (
              <div className="space-y-4">
                {applications.map((app) => (
                  <div key={app.id} className="border rounded-lg p-4">
                    <h3 className="font-bold text-lg">{app.users?.full_name}</h3>
                    <p className="text-gray-600">{app.users?.email}</p>
                    {app.users?.phone && <p className="text-gray-600">üìû {app.users.phone}</p>}
                    {app.cover_letter && (
                      <div className="mt-2">
                        <p className="font-semibold">Cover Letter:</p>
                        <p className="text-gray-700">{app.cover_letter}</p>
                      </div>
                    )}
                    {app.resume_url && (
                      <a 
                        href={app.resume_url} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-primary hover:underline mt-2 inline-block"
                      >
                        üìÑ View Resume
                      </a>
                    )}
                    <p className="text-sm text-gray-500 mt-2">
                      Status: <span className="font-semibold">{app.status}</span>
                    </p>
                    <p className="text-sm text-gray-500">Applied: {new Date(app.created_at).toLocaleDateString()}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default RecruiterDashboard;
